mapper.insert:
INSERT INTO XBT_BASELINE(
	PROC_YEAR,
	PROC_SEQ,
	PROC_NAME,
	EDU_START_DATE,
	EDU_END_DATE,
	TOT_STUDY_DATE,
	STUDY_LVL,
	LIMIT_PERSON_CNT,
	ENDING_STD_SCORE,
	PASS_SCORE,
	PASS_THEORY_SCORE,
	PASS_PRACTICE_SCORE,
	ENDING_PROCESS_END_YN,
	PRACTICE_TOTAL_SCORE,
	EVALUATION_TOTAL_SCORE,
	THEORY_TOTAL_SCORE,
	PRACTICE_HUMAN_TOTAL_SCORE,
	PRACTICE_CAR_TOTAL_SCORE,
	PRACTICE_HUMAN_SCORE,
	PRACTICE_CAR_SCORE,			
	INSERT_DATE,
	INSERT_ID,
	TEACHER_ID
)VALUES(
	 DATE_FORMAT(NOW(),'%Y')
	,#{procSeq}
	,#{procName}
	,#{eduStartDate}
	,#{eduEndDate}
	,#{totStudyDate}
	,#{studyLvl}
	,#{limitPersonCnt}
	,#{endingStdScore}
	,#{passScore}
	,#{passTheoryScore}
	,#{passPracticeScore}
	,'N'
	,#{practiceTotalScore}
	,#{evaluationTotalScore}
	,#{theoryTotalScore}
	,#{practiceHumanTotalScore}
	,#{practiceCarTotalScore}
	,#{practiceHumanScore}
	,#{practiceCarScore}			
	,now()
	,#{insertId}
	,#{teacherId}
)

INSERT INTO XBT_BASELINE_STUDENT_INFO(
	PROC_CD,
