	ENDING_YN,
	EDU_START_DATE,
	EDU_END_DATE,
	INSERT_DATE,
	INSERT_ID
)VALUES(
	#{procCd}
	,#{procYear}
	,#{procSeq}
	,#{eduCode}
	,#{eduName}			
	,#{userId}
	,#{userNm}
	,#{procNm}
	,#{compNm}
	,#{deptNm}
	,0
	,'ING'
	,#{endingProcessDate}
	,'N'
	,#{eduStartDate}
	,#{eduEndDate}
	,now()
	,#{insertId}
)

mapper.delete:
DELETE FROM XBT_STU_LEARNING_PROGRESSING 
WHERE PROC_CD = #{procCd} 
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

DELETE FROM XBT_BASELINE_LEARNING 
WHERE PROC_CD = #{procCd} 
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

mapper.select:

SELECT DISTINCT
	MODULE_ID,
   (SELECT MAX(MODULE_NM) FROM XBT_MODULE_INFO_MASTER T2 WHERE T1.MODULE_ID = T2.MODULE_ID)AS MODULE_NM
FROM XBT_BASELINE_EDU_DATE T1
WHERE PROC_CD = #{procCd}
AND EDU_START_DATE = #{eduStartDate}
AND EDU_END_DATE = #{eduEndDate}


SELECT DISTINCT
	MENU_CD,
	MENU_NM,
	MODULE_ID,
	MODULE_TYPE,
	LEARNING_TYPE
FROM XBT_BASELINE_EDU_DATE
WHERE PROC_CD = #{procCd}
AND EDU_START_DATE = #{eduStartDate}
AND EDU_END_DATE = #{eduEndDate}

mapper.insert:
INSERT INTO XBT_BASELINE_EDU_DATE(
	PROC_CD,
	PROC_NM,
	MODULE_ID,
	USER_ID,
	EDU_START_DATE,
	EDU_END_DATE,
	MENU_CD,
	MENU_NM,
	MODULE_TYPE,
	LEARNING_TYPE,
	INSERT_ID,
	INSERT_DATE
)VALUES(
	#{procCd}
	,#{procNm}
	,#{moduleId}
	,#{userId}
	,#{eduStartDate}
	,#{eduEndDate}
	,#{menuCd}
	,#{menuNm}
	,#{moduleType}
	,#{learningType}
	,#{insertId}
	,now()
)

mapper.update:

UPDATE XBT_BASELINE_EDU_DATE
SET 
	EDU_START_DATE = #{eduStartDateCopy},
	EDU_END_DATE = #{eduEndDateCopy}
WHERE PROC_CD = #{procCd}
AND EDU_START_DATE = #{eduStartDate}
AND EDU_START_DATE = #{eduEndDate}
