mapper.insert:
INSERT	INTO XBT_STU_MENU(
	P_MENU_CD,
	MENU_CD,
	MENU_NAME,
	MENU_URL,
	MENU_LEVEL,
	USE_YN,
	MENU_ORDER,
	LANGUAGE_CODE,
	MODULE_TYPE,
	LEARNING_TYPE,
	INSERT_ID,
	INSERT_DATE
)VALUES(
	#{pmenuCd}
	,#{menuCd}
	,#{menuName}
	,#{menuUrl}
	,'1'
	,'Y'
	,#{menuOrder}
	,#{languageCode}
	,#{moduleType}
	,#{learningType}
	,#{insertId}
	,now()
)

mapper.update:

UPDATE XBT_STU_MENU
SET 
	P_MENU_CD = #{pmenuCd}
	, MENU_CD = #{menuCd}
	, MENU_NAME = #{menuName}
	, USE_YN = #{useYn}
	, MENU_ORDER = #{menuOrder}
	, MODULE_TYPE = #{moduleType}
	, LEARNING_TYPE = #{learningType}
	, UPDATE_ID = #{updateId}
	, UPDATE_DATE = now()
WHERE MENU_CD = #{menuCd}
AND LANGUAGE_CODE = #{languageCode}

mapper.delete:
DELETE FROM XBT_STU_MENU 
WHERE MENU_CD = #{menuCd}
AND LANGUAGE_CODE = #{languageCode}

mapper.select:
SELECT 
   (
      SELECT MODULE_ID FROM XBT_MODULE_INFO_MASTER T2 WHERE T2.MODULE_ID = #{moduleId}
   )AS moduleId,
   (
      SELECT MODULE_NM FROM XBT_MODULE_INFO_MASTER T2 WHERE T2.MODULE_ID = #{moduleId}
   )AS moduleNm,          
   MENU_CD,
   MENU_NAME,
   MODULE_TYPE,
   LEARNING_TYPE
FROM XBT_STU_MENU T1
WHERE LANGUAGE_CODE = 'kr'
AND USE_YN = 'Y'
AND MENU_CD = #{menuCd}

SELECT THEORY_YN, EVALUATION_YN, PRACTICE_YN  FROM XBT_BASELINE_STUDENT_INFO
WHERE PROC_CD = #{procCd} AND USER_ID = #{userId}
AND EDU_CODE IN (4, 6)
