mapper.select:
SELECT GROUP_NO, GROUP_TYPE, THEORY_GROUP_CD, THEORY_GROUP_NAME, THEORY_PARENT_GROUP_CD, USE_YN, INSERT_ID, INSERT_DATE FROM XBT_THEORY_GROUP WHERE GROUP_TYPE = #{groupType}
AND THEORY_PARENT_GROUP_CD = #{theoryParentGroupCd}
ORDER BY INSERT_DATE DESC

mapper.insert:
INSERT INTO XBT_THEORY_GROUP(
	GROUP_TYPE,
	THEORY_GROUP_CD,
	THEORY_GROUP_NAME,
	THEORY_PARENT_GROUP_CD,
	USE_YN,
	INSERT_ID,
	INSERT_DATE
)VALUES(
	#{groupType}
	,#{theoryGroupCd}
	,#{theoryGroupName}
	,#{theoryParentGroupCd}
	,'Y'
	,#{insertId}
	,now()
)

mapper.update:

UPDATE XBT_THEORY_GROUP
SET 
	THEORY_GROUP_NAME = #{theoryGroupName},
	USE_YN = #{useYn}
WHERE GROUP_NO = #{groupNo}

mapper.insert:
INSERT INTO XBT_THEORY_QUESTION(
	QUESTION_ID,
	STUDY_LVL,
	QUESTION_TYPE,
	USE_YN,
	LAGE_GROUP_CD,
	MIDDLE_GROUP_CD,
	SMALL_GROUP_CD,
	QUESTION,
	CHOICE1,
	CHOICE2,
	CHOICE3,
	CHOICE4,
	MULTI_PLUS_IMG_NAME,
	ACTION_DIV,
	INSERT_ID,
	INSERT_DATE
)VALUES(
	#{questionId}
	,#{studyLvl}
	,#{questionType}
	,'Y'
	,#{lageGroupCd}
	,#{middleGroupCd}
	,#{smallGroupCd}
	,#{question}
	,#{choice1}
	,#{choice2}
	,#{choice3}
	,#{choice4}
	,#{multiPlusImgName}
	,#{actionDiv}
	,#{insertId}
	,now()
)

INSERT INTO XBT_THEORY_FILE
(
	TITLE,
	EDU_CODE,
	CONTENTS,
	USE_YN,
	INSERT_ID,
	INSERT_DATE
)
VALUES(
	#{title}
	,#{eduCode}
	,#{contents}
	,'Y'
	,#{insertId}
	,now()
)

mapper.update:

UPDATE XBT_THEORY_FILE
SET 
	TITLE = #{title}
	, EDU_CODE = #{eduCode}
	, CONTENTS = #{title}
	, USE_YN = #{useYn}
	, UPDATE_ID = #{updateId}
	, UPDATE_DATE = now()
WHERE THEORY_NO = #{theoryNo}
