mapper.select:
SELECT CODE_NO, LANGUAGE_CODE, GROUP_ID, CODE_ID, SORT_ORDER, CODE_NAME, CODE_VALUE, CODE_DESC, REMARKS, USE_YN, MAIN_YN, INSERT_ID, INSERT_DATE, UPDATE_ID, UPDATE_DATE FROM XBT_COMMON_CODE WHERE LANGUAGE_CODE = #{languageCode} AND GROUP_ID = #{groupId}
AND CODE_VALUE = #{codeValue}
AND CODE_NAME = #{codeName}

mapper.update:

UPDATE 
	XBT_COMMON_CODE
SET 
	LANGUAGE_CODE = #{languageCode}	
	,GROUP_ID	= #{groupId}	
	,CODE_ID	= #{codeId}	
	,SORT_ORDER	= #{sortOrder}	
	,CODE_NAME	= #{codeName}	
	,REMARKS	= #{remarks}	
	,USE_YN	= #{useYn}	
	,UPDATE_ID	= #{updateId}	
	,UPDATE_DATE	= now()	
	,CODE_DESC	= #{codeDesc}	
WHERE CODE_ID = #{codeId}

mapper.select:
SELECT CODE_NO, GROUP_ID ,CODE_NAME, CODE_VALUE, CODE_DESC ,USE_YN, LANGUAGE_CODE, SORT_ORDER FROM XBT_SYSTEM_CODE WHERE LANGUAGE_CODE= #{languageCode}
AND GROUP_ID = #{groupId}
AND USE_YN = 'Y' GROUP BY GROUP_ID, CODE_NO ,CODE_NAME, CODE_VALUE, CODE_DESC ,USE_YN, LANGUAGE_CODE, SORT_ORDER

SELECT CODE_NO, GROUP_ID ,CODE_NAME, CODE_VALUE, CODE_DESC , USE_YN, LANGUAGE_CODE, SORT_ORDER
FROM XBT_SYSTEM_CODE
WHERE
GROUP_ID = #{groupId} 
AND CODE_NAME = #{codeName} 
AND LANGUAGE_CODE= #{languageCode}
AND USE_YN  = 'Y'

mapper.update:

UPDATE 
	XBT_SYSTEM_CODE
SET 
	LANGUAGE_CODE = #{languageCode}	
	,GROUP_ID	= #{groupId}	
	,CODE_DESC	= #{codeDesc}	
	,CODE_NAME	= #{codeName}	
	,SORT_ORDER	= #{sortOrder}	
	,UPDATE_ID	= #{updateId}	
	,UPDATE_DATE	= now()	
WHERE CODE_NO = #{codeNo}
