mapper.select:
SELECT 
	COUNT(PROGRESS_NO) 
FROM XBT_STU_EVALUATION_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

SELECT 
	IFNULL(MAX(TRY_SEQ), 0) AS trySeq
FROM XBT_STU_EVALUATION_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}

SELECT 
	COUNT(PROGRESS_NO) 
FROM XBT_STU_EVALUATION_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear}
AND PROC_SEQ = #{procSeq}
and TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}	
AND END_YN = 'Y'

mapper.update:

UPDATE 
	XBT_BASELINE_EVALUATION 
SET END_YN = 'N'
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}

mapper.select:

SELECT 
	MODULE_ID, 
	PROC_CD, 
	PROC_YEAR, 
	PROC_SEQ, 
	USER_ID,
	BAG_SCAN_ID, 
	ACTION_DIV, 
	INSERT_DATE
FROM XBT_STU_EVALUATION_PROGRESSING 
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND END_YN = 'N'

SELECT 
	MAX(ACTION_DIV = #{userActionDiv}) as answer,  
	MAX(ACTION_DIV) as answerDiv
FROM XBT_STU_EVALUATION_PROGRESSING 
WHERE 
MODULE_ID = #{moduleId} 
and PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND BAG_SCAN_ID = #{bagScanId}
AND END_YN = 'N'

mapper.update:

UPDATE 
	XBT_STU_EVALUATION_PROGRESSING 
SET 
	USER_ACTION_DIV = #{userActionDiv},
	GAIN_SCORE = #{gainScore}
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND BAG_SCAN_ID = #{bagScanId}

mapper.select:
SELECT 
	floor(IFNULL(SUM(GAIN_SCORE), 0)/1000*1000/#{questionCnt}) AS gainScore 
FROM XBT_STU_EVALUATION_PROGRESSING 
WHERE 
MODULE_ID = #{moduleId} 
AND PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND END_YN = 'N'

SELECT
	COUNT(*)AS questionCnt,
	COUNT(CASE WHEN ACTION_DIV = USER_ACTION_DIV THEN 1 END) AS rightCnt,
	COUNT(CASE WHEN ACTION_DIV != USER_ACTION_DIV || USER_ACTION_DIV IS NULL THEN 1 END) AS wrongCnt	
FROM
	XBT_STU_EVALUATION_PROGRESSING
WHERE 
MODULE_ID = #{moduleId} 
AND PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}


SELECT COUNT(*) 
FROM XBT_BASELINE_EVALUATION
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

mapper.update:

UPDATE 
	XBT_BASELINE_EVALUATION 
SET 
	GAIN_SCORE = #{gainScore},
	PASS_YN = #{passYn},
	RIGHT_CNT = #{rightCnt},
	WRONG_CNT = #{wrongCnt}			
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
