mapper.update:
UPDATE RFT_USER
   SET LAST_LOGIN_DATE = now()
 WHERE LOGIN_ID = #{userId}

UPDATE RFT_USER SET 
	LOGIN_CNT = (SELECT COALESCE(LOGIN_CNT, 0)+1 FROM RFT_USER WHERE LOGIN_ID = #{userId})
WHERE LOGIN_ID = #{userId}

UPDATE XBT_STU_USER SET 
	USER_PW = #{userPw},
	PW_PRIOR = #{pwPrior},
	UPDATE_ID = #{userId},
	UPDATE_DATE = now(),
	PW_CHANGE = now()
WHERE USER_ID = #{userId}
AND HP_NO = #{hpNo} 
AND USER_NM = #{userNm}
AND USE_YN = 'Y'
