mapper.select:
SELECT 
	IFNULL(MAX(TRY_SEQ), 0) AS trySeq
FROM XBT_STU_THEORY_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}

SELECT 
	COUNT(PROGRESS_NO) 
FROM XBT_STU_THEORY_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear}
AND PROC_SEQ = #{procSeq}
AND USER_ID = #{userId}	
AND END_YN = 'Y'

SELECT 
	COUNT(PROGRESS_NO) 
FROM XBT_STU_THEORY_PROGRESSING
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND END_YN = #{endYn}

SELECT 
	DISTINCT (ACTION_DIV = #{userActionDiv}) as answer,  
	(ACTION_DIV) as answerDiv
FROM XBT_STU_THEORY_PROGRESSING 
WHERE 
PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND QUESTION_ID = #{questionId}

mapper.update:

UPDATE 
	XBT_STU_THEORY_PROGRESSING 
SET 
	USER_ACTION_DIV = #{userActionDiv},
	GAIN_SCORE = #{gainScore}
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
AND QUESTION_ID = #{questionId}

mapper.select:
SELECT COUNT(*) 
FROM XBT_BASELINE_THEORY
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

SELECT SUM(STUDY_LVL)/COUNT(STUDY_LVL) AS studyLvl FROM (
	SELECT STUDY_LVL FROM XBT_STU_THEORY_PROGRESSING 			
	WHERE 
	PROC_CD = #{procCd}  
	AND PROC_YEAR = #{procYear} 
	AND PROC_SEQ = #{procSeq}
	AND TRY_SEQ = #{trySeq}
	AND USER_ID = #{userId}
	GROUP BY STUDY_LVL
)T1

SELECT 
	IFNULL(SUM(GAIN_SCORE), 0) AS gainScore
FROM XBT_STU_THEORY_PROGRESSING 
WHERE 
PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

SELECT
	COUNT(*)AS questionCnt,
	COUNT(CASE WHEN ACTION_DIV = USER_ACTION_DIV THEN 1 END) AS rightCnt,
	COUNT(CASE WHEN ACTION_DIV != USER_ACTION_DIV || USER_ACTION_DIV IS NULL THEN 1 END) AS wrongCnt	
FROM
	XBT_STU_THEORY_PROGRESSING
WHERE 
PROC_CD = #{procCd}  
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}

mapper.update:

UPDATE 
	XBT_STU_THEORY_PROGRESSING 
SET 
	END_YN = #{endYn}
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}


UPDATE 
	XBT_BASELINE_THEORY 
SET 
	GAIN_SCORE = #{gainScore},
	PASS_YN = #{passYn},
	RIGHT_CNT = #{rightCnt},
	WRONG_CNT = #{wrongCnt}
WHERE PROC_CD = #{procCd} 
AND PROC_YEAR = #{procYear} 
AND PROC_SEQ = #{procSeq}
AND TRY_SEQ = #{trySeq}
AND USER_ID = #{userId}
